mapper.insert:
INSERT INTO doctor_info (
    user_id, doctor_no, department, title, qualification_cert, speciality,
    work_years, hospital, introduction, consultation_fee, online_status,
    rating, consultation_count, status, create_time, update_time, create_by, update_by, is_deleted
) VALUES (
    #{userId}, #{doctorNo}, #{department}, #{title}, #{qualificationCert}, #{speciality},
    #{workYears}, #{hospital}, #{introduction}, #{consultationFee}, #{onlineStatus},
    #{rating}, #{consultationCount}, #{status}, #{createTime}, #{updateTime}, #{createBy}, #{updateBy}, #{isDeleted}
)

mapper.update:
UPDATE doctor_info 
SET is_deleted = 1, update_time = NOW()
WHERE id = #{id}

UPDATE doctor_info 
SET is_deleted = 1, update_time = NOW()
WHERE user_id = #{userId}
